import { useContext } from "react";
import { ThemeProvider, ThemeContext } from "./Context";
import ThemeToggle from "./Toggle";
import "./index.css";

function MainContent() {
  const { dark } = useContext(ThemeContext);

  return (
    <div className={dark ? "dark" : "light"}>
      <h1>Hello</h1>
      <ThemeToggle />
    </div>
  );
}

function App() {
  return (
    <ThemeProvider>
      <MainContent />
    </ThemeProvider>
  );
}

export default App;
